const Header = (props) => {
  return (
    <h1>{props.name}</h1>
  )
}

const Content = (props) => {
  return (
    props.parts.map(p => <div><p>{p.name} {p.exercises}</p></div>)
  )
}

const Total = (props) => {

  return (
    <div>
      const total =  {props.parts.reduce((acc, object) => {
      return acc + object.exercises;
    }, 0)}

      {/* {props.parts.map(p => { total + p.exercises})} */}
      <p>Number of exercises: {this.total}</p>
    </div>
    // <p>{props.course.parts[0].name}</p>
    // <p>Number of exercises {props.course.parts[0].exercises + props.course.parts[1].exercises + props.course.parts[2].exercises}</p>
  )
}

const App = () => {
  const course = {
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10
      },
      {
        name: 'Using props to pass data',
        exercises: 7
      },
      {
        name: 'State of a component',
        exercises: 14
      }
    ]
  }

  const { name, parts } = course;

  return (
    <div>
      <Header name={name} />
      <Content parts={parts} />
      <Total parts={parts} />
    </div>
  )
}

export default App
